const codeblocks=document.querySelectorAll("codeblock");function addCopyButtonToCodeblock(t){const e=document.createElement("button");e.textContent="Copy",e.onclick=function(){copy(t,this)},t.appendChild(e)}function copy(t,e){let o=document.createElement("textarea");document.body.appendChild(o);let n=t.textContent;const c=e.textContent;n.endsWith(c)&&(n=n.slice(0,-c.length).trim()),o.value=n,o.select(),document.execCommand("copy"),document.body.removeChild(o),e.textContent="Copied!",setTimeout((function(){e.textContent="Copy"}),1e3)}codeblocks.forEach((t=>{addCopyButtonToCodeblock(t)}));
