const switchy=document.getElementById("switchy");function moveIndicator(){switchy.classList.toggle("active")}function switchyDarkTheme(){if(document.documentElement.classList.contains("dark")){document.documentElement.classList.remove("dark"),document.documentElement.classList.add("light");for(let e of document.getElementsByClassName("card"))e.classList.remove("card-dark-theme"),e.classList.add("card-light-theme")}else if(document.documentElement.classList.contains("light")){document.documentElement.classList.remove("light"),document.documentElement.classList.add("dark");for(let e of document.getElementsByClassName("card"))e.classList.remove("card-light-theme"),e.classList.add("card-dark-theme")}else if(window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches){document.documentElement.classList.add("light");for(let e of document.getElementsByClassName("card"))e.classList.remove("card-dark-theme"),e.classList.add("card-light-theme")}else{document.documentElement.classList.add("dark");for(let e of document.getElementsByClassName("card"))e.classList.remove("card-light-theme"),e.classList.add("card-dark-theme")}}function cssHoverEffect(){if(window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches)for(let e of document.getElementsByClassName("card"))e.classList.add("card-dark-theme");else for(let e of document.getElementsByClassName("card"))e.classList.add("card-light-theme")}switchy.addEventListener("click",moveIndicator,!1),cssHoverEffect();
