function menuToggle(t){t.classList.toggle("change"),document.getElementById("container-button").classList.toggle("show")}window.onclick=function(t){if(!(t.target.matches("#menu-title")||t.target.matches("#menu-button")||t.target.matches(".bar")||t.target.matches(".dropdown-content")||t.target.matches("#switchyContainerAbsolute")||t.target.matches("#theme")||t.target.matches("#switchy")||t.target.matches(".indicator")||t.target.matches("#editorModeContainerAbsolute")||t.target.matches("#edit-doc")||t.target.matches("#editor-mode")||t.target.matches("#screenshotBtn"))){const t=document.getElementsByClassName("dropdown-content");for(let e of t){e.classList.contains("show")&&e.classList.remove("show");const t=document.getElementById("menu-container");t.classList.contains("change")&&t.classList.remove("change")}}};
